package com.education.mapper.common.utils;import com.education.mapper.common.model.ModelBeanMap;import java.util.ArrayList;import java.util.List;/** * @author zengjintao * @create 2019/7/12 20:12 * @since 1.0 **/public class MapTreeUtils {    public static List<ModelBeanMap> buildTreeData(List<ModelBeanMap> dataList) {        List<ModelBeanMap> result = new ArrayList<>();        List<ModelBeanMap> parentList = getRoot(dataList);        for (ModelBeanMap tree : parentList) {            Integer id = (Integer) tree.get("value");            if (ObjectUtils.isEmpty(id)) {                id = (Integer) tree.get("id");            }            List<ModelBeanMap> children = getChildrenTree(dataList, id);            if (ObjectUtils.isNotEmpty(children)) {                tree.put("children", children);            }            result.add(tree);        }        return result;    }    public static List<ModelBeanMap> getChildrenTree(List<ModelBeanMap> dataList, int parentId) {        List<ModelBeanMap> childrenTree = new ArrayList();        dataList.forEach(data -> {            if ((Integer)data.get("parent_id") == parentId) {                childrenTree.add(data);            }        });        childrenTree.forEach(data -> {            Integer id = (Integer) data.get("value");            if (ObjectUtils.isEmpty(id)) {                id = (Integer) data.get("id");            }            List<ModelBeanMap> children = getChildrenTree(dataList, id);            if (ObjectUtils.isNotEmpty(children)) {                data.put("children", children);            }        });        return childrenTree;    }    /**     * 获取顶级父类数据     * @param dataList     * @return     */    private static List<ModelBeanMap> getRoot(List<ModelBeanMap> dataList) {        List<ModelBeanMap> rootList = new ArrayList<>();        dataList.forEach(root -> {            if ((Integer)root.get("parent_id") == 0) {                rootList.add(root);            }        });        return rootList;    }}