package com.education.event.impl;import com.education.event.BaseTask;import com.education.common.component.SpringBeanManager;import com.education.service.system.SystemMessageInfoMessageService;/** * 上课微信模板消息推送 * @author zengjintao * @create 2019/5/17 16:20 * @since 1.0 **/public class StudentMessageTask extends BaseTask {    private SystemMessageInfoMessageService systemMessageInfoMessageService;    public StudentMessageTask() {        systemMessageInfoMessageService = SpringBeanManager.getBean(SystemMessageInfoMessageService.class);    }    @Override    public void run() {        systemMessageInfoMessageService.save(getModelBeanMap());    }}